
function zoomImage() {

	$(document).find('.item_ids').each(function(index, el) {

		var each_id = each_num = $(el).find('.NZoomImgClass').prop('id');
		console.log("each_id", each_id);

		each_num = each_num.replace('NZoomImg', '');
		console.log("each_num", each_num);

		try{
			let t=document.getElementById(each_id),
			e=t.getAttribute("data-NZoomscale")<=0?1:t.getAttribute("data-NZoomscale"),
			s=t.clientWidth,o=t.clientHeight;
			$(document).find("#"+each_id).replaceWith('<div class="NZoomContainer'+each_num+'">'+t.outerHTML+"</div>");
			let i=$(".NZoomContainer"+each_num),n=$(document).find("#"+each_id);
			i.css("width",s+"px"),i.css("height",o+"px"),
			i.mousemove(function(t){
				let e=$(this).offset(),
				i=(t.pageX-e.left)/s*100<=100?(t.pageX-e.left)/s*100:100,c=(t.pageY-e.top)/o*100<=100?(t.pageY-e.top)/o*100:100;
				n.css("transform-origin",i+"% "+c+"%")
			}),
			i.mouseenter(function(){
				n.css("cursor","crosshair"),
				n.css("width",s+"px"),
				n.css("height",o+"px"),
				n.css("transition","0.2s"),
				n.css("transform","scale("+e+")")
			}).mouseleave(function(){
				n.css("transition","0.2s"),
				n.css("transform","scale(1)")
			})

		}catch(e){
			console.log("error", e);
		}
		
	});
}
$(document).ready(function(){

	zoomImage();


	
});